    static getDerivedStateFromProps(nextProps: any, prevState: any) {
        if (nextProps.tradieProfileViewData && !_.isEqual(nextProps.tradieProfileViewData, prevState.tradieInfo)) {
            return {
                tradieInfo: nextProps.tradieProfileViewData
            }
        }
        return null;
    }

