    submitUpdateProfile = async () => {
        const newSpecialization: Array<any> = [...this.state.profileViewData?.areasOfSpecialization?.specializationData];
        var newSpecializationData: Array<any> = newSpecialization?.map(({ specializationId }: { specializationId: string }) => specializationId?.length > 0 && specializationId);
        const data = {
            fullName: this.state.basicDetailsData?.fullName,
            userImage: this.state.profileViewData?.tradieImage || this.state.profileViewData?.builderImage,
            trade: [this.state.profileViewData?.areasOfSpecialization?.tradeData[0]?.tradeId],
            specialization: newSpecializationData,
            about: this.userType === 1 ? this.state.profileViewData?.about || '' : undefined,
            position: this.userType === 1 ? undefined : this.state.profileViewData?.position,
            companyName: this.userType === 1 ? undefined : this.state.profileViewData?.companyName,
            aboutCompany: this.userType === 1 ? undefined : this.state.profileViewData?.about || this.state.profileViewData?.aboutCompany,
        }
        if (this.state.formData) {
            const res1 = await onFileUpload(this.state.formData);
            if (res1?.success) {
                data.userImage = res1?.imgUrl;
            }
        }
        const res2 = await tradieUpdateProfileDetails(data);
        if (res2?.success) {
            if (this.state.formData) {
                updateChatUserDetails('userImage', data.userImage);
            }
            this.setState({
                formData: null,
                isProfileViewDataChanged: false
            });
            this.userType === 1 ? this.props.callTradieProfileData() : this.props.getProfileBuilder();
            if (this.userType === 2 && this.state.about && this.state.profileViewData?.companyName !== this.state.about) {
                moengage.moE_SendEvent(MoEConstants.ADDED_INFO_ABOUT_COMPANY, { timeStamp: moengage.getCurrentTimeStamp() });
                mixPanel.mixP_SendEvent(MoEConstants.ADDED_INFO_ABOUT_COMPANY, { timeStamp: moengage.getCurrentTimeStamp() });
            }
        }
    }

