import { useState, useEffect } from "react";
import { NavLink, useHistory } from "react-router-dom";
import InfiniteScroll from "react-infinite-scroll-component";

import dummy from "../../../assets/images/u_placeholder.jpg";
import noDataFound from "../../../assets/images/checked-1.png";

interface Proptypes {
  loading: boolean;
  newJobsCount: number;
  appliedJobList: any;
  getAppliedJobList: (page: number) => void;
  resetAppliedJobList: () => void;
}

const AppliedJobs = ({
  loading,
  getAppliedJobList,
  appliedJobList,
  newJobsCount,
  resetAppliedJobList,
}: Proptypes) => {
  const [jobList, setJobList] = useState<Array<any>>([]);
  const [pageNo, setPageNo] = useState<number>(1);
  const [hasMoreItems, setHasMoreItems] = useState<boolean>(true);
  const [isLoad, setIsLoad] = useState(true);
  const history: any = useHistory();
  let totalJobsCount: number = newJobsCount;
  console.log(
    totalJobsCount,
    "totalJobsCount",
    jobList,
    "jobList",
    hasMoreItems,
    "hasMoreItems"
  );

  useEffect(() => {
    callJobList();

    return () => resetAppliedJobList();
  }, []);

  const callJobList = async () => {
    // if (newJobsCount && jobList.length >= totalJobsCount) {
    //   setHasMoreItems(false);
    //   return;
    // }
    getAppliedJobList(pageNo);
  };

  useEffect(() => {
    if (appliedJobList?.length || Array.isArray(appliedJobList)) {
      const allJobs = [...jobList, ...appliedJobList];
      console.log(
        jobList,
        "jobList",
        appliedJobList,
        "props.appliedJobList",
        allJobs,
        "allJobs"
      );
      setJobList(allJobs);
      setIsLoad(false);
      setPageNo(pageNo + 1);
      if (appliedJobList.length < 10) {
        setHasMoreItems(false);
      }
      resetAppliedJobList();
    }
  }, [appliedJobList]);

  return (
    <div className="detail_col">
      <InfiniteScroll
        dataLength={jobList.length}
        next={callJobList}
        style={{ overflowX: "hidden" }}
        hasMore={hasMoreItems}
        loader={<></>}
      >
        <span className="sub_title">Applied Jobs</span>
        <div className="flex_row tradies_row">
          {!isLoad && !loading && jobList.length
            ? jobList.map(
                ({
                  jobId,
                  tradeSelectedUrl,
                  tradeId,
                  specializationId,
                  jobName,
                  tradeName,
                  time,
                  amount,
                  locationName,
                  durations,
                  milestoneNumber,
                  totalMilestones,
                  status,
                  builderName,
                  builderImage,
                  quoteJob,
                  quoteSent,
                }) => (
                  <div className="flex_col_sm_6">
                    <div
                      className="tradie_card"
                      data-aos="fade-in"
                      data-aos-delay="250"
                      data-aos-duration="1000"
                    >
                      <NavLink
                        to={`/job-details-page?jobId=${jobId}&redirect_from=jobs`}
                        className="more_detail circle"
                      ></NavLink>
                      <div className="user_wrap">
                        <figure className="u_img">
                          <img
                            src={builderImage || dummy}
                            alt=""
                            onError={(e: any) => {
                              if (e?.target?.onerror) {
                                e.target.onerror = null;
                              }
                              if (e?.target?.src) {
                                e.target.src = dummy;
                              }
                            }}
                          />
                        </figure>
                        <div className="details">
                          <span className="name">{jobName}</span>
                          <span className="prof">{builderName}</span>
                        </div>
                      </div>
                      <div className="job_info">
                        <ul>
                          <li className="icon clock">{time}</li>
                          <li className="icon dollar">{amount}</li>
                          <li className="icon location line-1">
                            {locationName}
                          </li>
                          <li className="icon calendar">{durations}</li>
                        </ul>
                      </div>
                      <div
                        className="job_progress_wrap"
                        id="scroll-progress-bar"
                      >
                        <div className="progress_wrapper">
                          <span className="completed-digit" id="digit-progress">
                            <b>Job Milestones {milestoneNumber}</b> of{" "}
                            {totalMilestones}
                          </span>
                          <span className="progress_bar">
                            <input
                              className="done_progress"
                              id="progress-bar"
                              type="range"
                              min="0"
                              value={milestoneNumber}
                              max={totalMilestones}
                            />
                          </span>
                        </div>
                      </div>
                      {quoteJob && (
                        <div
                          className="quote-text"
                          onClick={() => {
                            history.push({
                              pathname: `/quote-job`,
                              state: {
                                jobData: {
                                  jobId: jobId,
                                  tradeId: tradeId,
                                  specializationId: specializationId,
                                },
                                redirect_from: "appliedJobs",
                              },
                            });
                          }}
                        >
                          {"Quote sent"}
                        </div>
                      )}
                    </div>
                  </div>
                )
              )
            : !isLoad &&
              !loading && (
                <div className="no_record  m-t-vh">
                  <figure>
                    <span
                      style={{
                        height: "85px",
                        width: "85px",
                        backgroundColor: "#161D4A",
                        borderRadius: "50%",
                        display: "inline-block",
                        position: "relative",
                      }}
                    ></span>
                    <img
                      src={star}
                      style={{
                        position: "absolute",
                        left: "49%",
                        top: "13%",
                        height: "24px",
                      }}
                      alt="data not found"
                    />
                  </figure>

                  <span
                    style={{
                      fontSize: " 20px",
                      alignItems: "center",
                      display: "inline-block",
                      lineHeight: "20px",
                      fontWeight: "bold",
                      color: "#161D4A",
                      marginTop: "20px",
                    }}
                  >
                    You don't have any active job yet
                  </span>
                  <button
                    style={{
                      padding: "19px 32px",
                      width: "327px",
                      borderRadius: " 3px",
                      backgroundColor: "#FEE600",
                      textAlign: "center",
                      fontSize: "16px",
                      fontWeight: "bold",
                      color: "#161D4A",
                      marginTop: "20px",
                    }}
                  >
                    View recommened jobs
                  </button>
                </div>
              )}
        </div>
      </InfiniteScroll>
    </div>
  );
};

export default AppliedJobs;
